import {Link, useMatch, useResolvedPath} from "react-router-dom";
import HomeLink from './navbar_components/logo_homelink.js';
import HamburgerMenu from './navbar_components/HamburgerMenu.js';
import React from 'react'

const Navbar = () =>
{

    return (
        <nav className="flex wrap flex-row w-screen sticky top-0 z-50 justify-between items-center bg-background-blue px-[4%] gap-[10%] min-h-[60px]">
            <Link to="/">
                <HomeLink />
            </Link>
            

            <ul className="hidden md:flex flex-row gap-5 font-opensans items-center text-[1vw] my-[1vw] min-h-[40px]">
                <ul className="hidden md:flex justify-center flex-row gap-[1.3vw] my-[1vw]">
                    <CustomLink to="/aboutus">About</CustomLink>
                    <CustomLink to="/events">Events</CustomLink>
                    <CustomLink to="/contactus">Contact</CustomLink>
                </ul>

                <li>
                    <Link className="flex font-bold px-[1vw] py-[0.5vw] w-[100%] justify-center items-center transition hover:-translate-y-1 hover:scale-100 border-2 border-[#158ba8] text-[#158ba8] ease-in-out delay-10 bg-white hover:text-white hover:bg-[#158ba8] duration-300" to="/donate">
                        DONATE
                    </Link>
                </li>
            </ul>
            <HamburgerMenu/>
        </nav>
    )
}

function CustomLink({ to, children })
{
    const resolvedPath = useResolvedPath(to)
    const isActive = useMatch({ path: resolvedPath.pathname, end: true})
    return (
        <li className="flex font-lato font-medium text-[16px] lg:text-[1.6vw]">
            <Link to={to} className={"transition text-white ease-in-out hover:-translate-y-1 hover:scale-100 duration-[125ms] hover:text-orange-300 hover:font-medium " + (isActive ? "text-orange-200" : "") + " duration-125"}>{children}</Link>
        </li>
    )
} 

export default Navbar;
