import {Link, useMatch, useResolvedPath} from "react-router-dom";
import { useState } from 'react';
import { Sling as Hamburger } from 'hamburger-react'

const HamburgerMenu = () => {
    const [navbarOpen, setNavbarOpen] = useState(false);
    return (
        <div className="flex md:hidden w-[24px]">
            <button onClick={ () => setNavbarOpen(prev => !prev) }>
                <Hamburger rounded size={22}/>
            </button>
            <ul className={"flex absolute flex-col " + (navbarOpen ? "hidden" : "")}>
                <CustomLink to="/">Home</CustomLink>
                <CustomLink to="/aboutus">About Us</CustomLink>
                <CustomLink to="/contact">Contact</CustomLink>
                <CustomLink to="/events">Events</CustomLink>
                <CustomLink to="/login">Login</CustomLink>
                <CustomLink to="/donate">Donate</CustomLink>
            </ul>
        </div>
    )
}

function CustomLink({ to, children })
{
    const resolvedPath = useResolvedPath(to)
    const isActive = useMatch({ path: resolvedPath.pathname, end: true})
    return (
        <li className="flex font-lato font-black text-[1.4vw]">
            <Link to={to} className={"transition ease-in-out hover:border-b-4 hover:border-defpink duration-[125ms] hover:text-pink-400 hover:font-medium " + (isActive ? "border-b-4 border-defpink" : "") + " duration-125"}>{children}</Link>
        </li>
    )
} 

export default HamburgerMenu;
