import {  useLocation } from "react-router-dom";
import hamburgericon from './hamburgericon.svg';
import { useState, useEffect } from 'react';
import { CSSTransition } from "react-transition-group";


import Menu from './navbar_components_components/Menu.js'
import './HamburgerMenu.css'

const HamburgerMenu = (props) => {
    const [open, setOpen] = useState(false);
    const [activeMenu, setActiveMenu] = useState('not-open');

    const location = useLocation();

    useEffect(() => {
        setOpen(false);
    }, [location]);

    const [menuHeight, setMenuHeight] = useState(null);

    function calcHeight(el) {
        const height = el.offsetHeight;
        setMenuHeight(height);
    }

    return (
        <div className="flex justify-center items-center rounded-full md:hidden w-[35px] h-[35px] border-2 border-slate-300 ">
            <button onClick={ () => { 
                setOpen(prev => !prev);
                setActiveMenu('main');
            } }>
                <img className="" src={hamburgericon} id="hamburger" alt="hamburger"/>
            </button>

            <div className={(open) ? "absolute top-[55px] w-[250px] translate-x-[-45%] bg-white rounded-md p-4 overflow-hidden border-2 border-gray-400 dropdown" : "hidden"} style={{ height: menuHeight }}>
                <CSSTransition in={activeMenu === 'main'} unmountOnExit timeout={500} classNames="menu-primary" onEnter={calcHeight}>
                    <Menu />
                </CSSTransition>
            </div>

        </div>
    )
}

export default HamburgerMenu;
