import {Link, useMatch, useResolvedPath} from "react-router-dom";
import hamburgericon from './hamburgericon.svg';
import { useState } from 'react';

const HamburgerMenu = (props) => {
    const [open, setOpen] = useState(false);

    return (
        <div className="flex md:hidden w-[24px]">
            <button onClick={ () => setOpen(!open) }>
                <img className="" src={hamburgericon} id="hamburger" alt="hamburger"/>
            </button>

            <div className={(open) ? "absolute top-[58px] width-[300px] translate-x-[-45%] bg-white-transparent rounded-md p-4 overflow-hidden border-2 border-gray-400" : "hidden"}>
                <CustomLink to="/aboutus">About Us</CustomLink>
            </div>
        </div>
    )
}

function CustomLink({ to, children })
{
    const resolvedPath = useResolvedPath(to)
    const isActive = useMatch({ path: resolvedPath.pathname, end: true})
    return (
        <li className="flex font-lato font-black text-[1.4vw]">
            <Link to={to} className={"transition ease-in-out hover:border-b-2 hover:border-defpink hover:border-dashed duration-[125ms] " + (isActive ? "border-b-2 border-defpink" : "") + " duration-125"}>{children}</Link>
        </li>
    )
} 

export default HamburgerMenu;
