import {Link, useMatch, useResolvedPath} from "react-router-dom";
import hamburgericon from './hamburgericon.svg';
import { useState } from 'react';

import { CSSTransition } from 'react-transition-group'

const HamburgerMenu = (props) => {
    const [open, setOpen] = useState(false);

    return (
        <div className="flex md:hidden w-[24px]">
            <button onClick={ () => setOpen(!open) }>
                <img className="" src={hamburgericon} id="hamburger" alt="hamburger"/>
            </button>

            <div className={(open) ? "absolute top-[58px] w-[20vw] translate-x-[-75%] bg-white-transparent rounded-md p-4 overflow-hidden border-2 border-gray-400" : "hidden"}>
                <Menu />
            </div>

        </div>
    )
}

function Menu()
{
    const [activeMenu, setActiveMenu] = useState('main');

    return (
        <div>
            <CSSTransition in={activeMenu === 'main'} unmountOnExit timeout={500}>

                <CustomLink to="/">Home</CustomLink>
                <CustomLink to="/donate">Donate</CustomLink>
                <CustomLink to="/events">Events</CustomLink>
                <CustomLink to="/login">Login</CustomLink>
                <CustomLink to="/signup">Sign Up</CustomLink>

            </CSSTransition>
        </div>
    )
}

function CustomLink({ to, children })
{
    const resolvedPath = useResolvedPath(to)
    const isActive = useMatch({ path: resolvedPath.pathname, end: true})
    return (
        <li className="flex font-lato font-black text-[2vw]">
            <Link to={to} className={"transition rounded-md w-full ease-in-out p-2 hover:bg-gray-200 duration-[150ms] " + (isActive ? "bg-gray-300" : "")}>{children}</Link>
        </li>
    )
} 

export default HamburgerMenu;
